import React, { useReducer,useState, useRef } from 'react';
import './App.css';
import * as mobilenet from '@tensorflow-models/mobilenet';

const stateMachine = {
  initial :'initial',
  states : { 
  initial : { on : {next : 'loadingModel'}},
  loadingModel: { on : {next: 'awaitingUpload'}},
  awaitingUpload:{ on : {next: 'ready'}},
  ready : { on : {next: 'classifying'}},
  classifying: { on : {next: 'complete'}},
  complete : { on : {next: 'awaitingUpload'}}
}
}
const reducer = (currentState, event) => stateMachine.states[currentState].on[event] || stateMachine.initial;

function App() {
  const [state,dispatch] = useReducer(reducer,stateMachine.initial);
  const [model,setModel] = useState(null);
  const [imageURL,setImageUrl] = useState(null);
  const [results,setResults] = useState([]);
  const inputRef = useRef();
  const imageRef = useRef();

  const next = () => dispatch('next')


  const loadModel = async () => {
    next();
    const mobilenetModel = await mobilenet.load();
    setModel(mobilenetModel);
    next();

  }
  const handleUpload = e => {
    const { files } = e.target;
    if (files.length > 0){
      const url = URL.createObjectURL(files[0]);
      setImageUrl(url);
      next();
    }
  }

  const identify = async () => {
    next();
    const results = await model.classify(imageRef.current);
    setResults(results)
    next();
  }
  const buttonProps = {
    initial : { text : 'Load Model', actions: loadModel },
    loadingModel : { text : 'Loading Model...', actions: () => {} },
    awaitingUpload : {  text : 'Upload Photo', actions: () => inputRef.current.click()},
    ready : {  text : 'Identifier', actions: () => {}},
    classifying : {  text : 'Identifying', actions: () => {}},
    complete : {  text : 'Reset', actions: () => {}}
  }

  const { showImage = false } = stateMachine.states[state];
  return (
    <div>
      {showImage && <img  src={imageURL} alt="upload-preview" ref={imageRef} /> }
      <input type="file" accept="image/*" capture="camera" onChange={handleUpload} ref={inputRef} />
      <button onClick={buttonProps[state].action}> {buttonProps[state].text}</button>
    </div>
  );
}

export default App;
