import React, { useReducer } from 'react';
import './App.css';
import * as mobilenet from '@tensorflow-models/mobilenet';

const stateMachine = {
  initial :'initial',
  states : { 
  initial : { on : {next : 'loadingModel'}},
  loadingModel: { on : {next: 'awaitingUpload'}},
  awaitingUpload:{ on : {next: 'ready'}},
  ready : { on : {next: 'classifying'}},
  classifying: { on : {next: 'complete'}},
  complete : { on : {next: 'awaitingUpload'}}
}
}
const reducer = (currentState, event) => stateMachine.states[currentState].on[event] || stateMachine.initial;

function App() {
  const [state,dispatch] = useReducer(reducer,stateMachine.initial);
  const next = () => dispatch('next')

  const buttonProps = {
    initial : { text : 'Load Model', actions: () => {} },
    loadingModel : { text : 'Loading Model...', actions: () => {}},
    awaitingUpload : {  text : 'Upload Photo', actions: () => {}},
    ready : {  text : 'Identifier', actions: () => {}},
    classifying : {  text : 'Identifying', actions: () => {}},
    complete : {  text : 'Reset', actions: () => {}}
  }
  return (
    <div>
      <button onClick={() => dispatch('next')}>{state}</button>
    </div>
  );
}

export default App;
